.container {
    background: #2f87ed;
    padding: 20px;
    display: flex;
    justify-content: center;
  }
  
  .cardContainer {
    display: flex;
    gap: 10px;
    margin-top: 15px;
    flex-wrap: wrap;
    @media screen and (max-width: 768px) {
      gap: 10px;
      flex-direction: column;
    }
  }
  
  .bg {
    background-image: linear-gradient(to right, #6a11cb 0%, #2575fc 100%);
    margin: 20px auto;
    width: 90%;
    box-shadow: rgba(0, 0, 0, 0.56) 0px 22px 70px 4px;
    border: 6px solid black;
    font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
    border-radius: 15px;
    padding: 20px;
    text-align: center;
    h1 {
      color: #ffc107;
      margin-bottom: 10px;
      &:hover {
        transform: translateY(-10px);
        transition: transform 0.5s linear;
        cursor: pointer;
      }
    }
    p {
      color: white;
      font-weight: bolder;
      font-size: larger;
    }
    @media screen and (max-width:768px) {
      padding-top: 5%;
    }
  }
  
  .card {
    position: relative;
    width: 24%;
    height: 50vh;
    margin-bottom: 20px;
    border-radius: 15px;
    overflow: hidden;
    transition: transform 0.5s ease, box-shadow 0.5s ease;
    box-shadow: rgba(0, 0, 0, 0.1) 0px 4px 6px, rgba(0, 0, 0, 0.1) 0px 1px 3px;
    
    &:hover {
      transform: scale(1.05);
      box-shadow: rgba(0, 0, 0, 0.3) 0px 10px 20px;
      cursor: pointer;
    }
  
    @media screen and (max-width: 821px) {
      height: 26vh;
    }
    @media screen and (max-width: 768px) {
      height: 33vh;
      width: 100%;
    }
    @media screen and (max-width: 400px) {
      height: 40vh;
    }
  
    img {
      width: 100%;
      height: 60%;
      object-fit: cover;
      transition: opacity 0.5s ease;
      &:hover {
        opacity: 0.7;
      }
    }
  
    h1 {
      color: white;
      position: absolute;
      bottom: 10px;
      left: 50%;
      transform: translateX(-50%);
      font-size: 23px;
      text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.6);
      @media screen and (max-width: 650px) {
        font-size: 20px;
      }
      @media screen and (max-width: 400px) {
        font-size: 18px;
      }
    }
    
    .overlay {
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background: rgba(0, 0, 0, 0.4);
      opacity: 0;
      transition: opacity 0.3s ease;
      display: flex;
      justify-content: center;
      align-items: center;
      color: white;
      font-size: 18px;
      text-align: center;
      padding: 10px;
      border-radius: 15px;
      h2 {
        margin: 0;
      }
    }
  
    &:hover .overlay {
      opacity: 1;
    }
  }
  
  .links {
    height: 100%;
    text-decoration: none;
    display: flex;
    justify-content: center;
    align-items: center;
    &:hover {
      transform: scale(1.07);
      transition: transform 0.5s ease-in-out;
    }
  }
