commits.content:
.container {
  background: #2f87ed;
}

.cardContainer {
  display: flex;
  gap: 10px; // Updated to use gap for spacing
  margin-top: 10px;
  width: 98%;
  @media screen and (max-width: 768px) {
    flex-direction: column;
  }
}

.bg {
  background-image: linear-gradient(to right, #6a11cb 0%, #2575fc 100%);
  margin: 0 auto;
  margin-top: 20px;
  width: 90%;
  align-items: center;
  box-shadow: rgba(0, 0, 0, 0.56) 0px 22px 70px 4px;
  border: 6px solid black;
  font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
  border-radius: 15px;
  h1 {
    color: #ffc107;
    align-items: center;
    text-align: center;
    padding-top: 2%;
    &:hover {
      transform: translateY(-10px);
      transition: transform 0.5s linear;
      cursor: pointer;
    }
  }
  p {
    color: white;
    text-align: center;
    padding-top: 0%;
    font-weight: bolder;
    font-size: larger;
  }
}

.card {
  display: flex;
  flex-direction: column;
  width: 24%;
  height: 50vh;
  position: relative;
  margin-left: 1.4%;
  margin-bottom: 2%;
  &.inView {
    animation: wave 2s ease-in-out forwards;
  }
  &:hover {
    transform: scale(1.2);
    cursor: pointer;
  }
  @keyframes wave {
    0% {
      opacity: 0;
      transform: translateX(-100px);
    }
    25% {
      opacity: 1;
      transform: translateX(-70px) translateY(-30px);
    }
    75% {
      opacity: 1;
      transform: translateX(-40px) translateY(0px);
    }
    100% {
      opacity: 1;
      transform: translate(0px, 0px);
    }
  }
  @media screen and (max-width: 821px) {
    height: 26vh;
   }
  @media screen and (max-width: 768px) {
    margin-left: 0%;
    height: 33vh;
    width: 100%;
  }
  @media screen and (max-width: 380px) {

    overflow: hidden;
  
  }
  img {
    align-items: center;
    margin: 0 auto;
    width: 100%;
    position: absolute;
  }
  h1 {
    color: white;
    position: relative;
    padding-top: 0%;
    font-size: 22px;
    top: 80%;
    @media screen and (max-width: 768px) {
      // font-size: 20px;
      top: 82%;
      color: white;
      // margin-bottom: 75px;
      font-size: 22px;
    }
  }
}

.links {
  height: 100%;
  text-decoration: none;
  &:hover {
    transform: scale(1.07);
    transition: transform 0.5s ease-in-out;
  }
  @media screen and (max-width:768px) {
    .img{
      width: 85%;
      left: 8%;
    }
  }
 
}
