.navbar_cain{
  position: fixed;
  width: 100%;
  z-index: 10000;
}
.caintainer {
  display: flex;
  justify-content: end;
  padding: 30px;
  color: white;
  background: rgb(2,0,36);
  background: linear-gradient(90deg, rgba(2,0,36,1) 0%, rgba(9,9,121,1) 46%, rgba(0,212,255,1) 100%);
  z-index: 40;
  gap: 30px;
  // position: fixed; // Commented out for better control with JavaScript
}

.search_box {
  width: 100%;
    position: absolute;
    display: flex;
    top: 20px;
    left: 100px;
  @media screen  and (max-width:768px){
      left: 20px;
  }
}

.input {
  padding: 10px;
  width: 30%;
  height: 30px;
  border: 2px solid black;
  border-radius: 5px;
  @media screen and (max-width:768px) {
    font-size: 20px;
  }
}

.items {
  position: relative;
  right: 80px;
  font-size: 18px;
}

.name {
  display: flex;
}

.name p {
  cursor: pointer;
}

.name p:hover {
  transform: scale(1.2);
  transition: transform 0.3s linear;
}

.line {
  margin-left: 10px;
}

.hamburger {
  display: none;
  font-size: 24px;
  cursor: pointer;
  position: absolute;
  left: 20px;
  top: 20px;
  z-index: 50;
}

// Add this class to control body overflow when menu is open
.no-scroll {
  overflow: hidden;
}

@media screen and (max-width: 768px) {
  .caintainer {
    display: none;
  }
  
  .hamburger {
    display: block;
    color: white;
  }
  
  .line {
    display: none;
  }
  
  .name {
    position: absolute;
    top: 100px;
    width: 100%;
    font-size: 30px;
  }
  
  .name p {
    border-bottom: 1px solid white;
    border-top: 1px solid white;
  }
  
  .showMenu {
    display: flex;
    flex-direction: column;
    justify-content: start;
    gap: 80px;
    align-items: center;
    top: 0;
    height: 100vh;
    width: 70%;
    list-style-type: none;
    background: rgb(2,0,36);
    background: linear-gradient(90deg, rgba(2,0,36,1) 0%, rgba(9,9,121,1) 46%, rgba(0,212,255,1) 100%);
    position: fixed; // Make sure the menu covers the viewport
    left: 0; // Align the menu to the left
    z-index: 40; // Ensure it's above other content
    animation: come 0.5s ease-in-out 1 forwards;
  }
  @keyframes come {
      0%{
        width: 40%;
        opacity: 0;
      }
      100%{
        width: 100%;
        opacity: 1;
      }
  }
  
  .search_box {
    width: 100%;
    position: absolute;
    display: flex;
    justify-content: center;
    top: 20px;
    right: 5px;
    @media screen and (max-width:768px) {
      width: 100%;
      position: absolute;
      display: flex;
      justify-content: center;
      top: 0px;
      left: 0px;
      padding: 13px;
      background: linear-gradient(
        90deg,
        rgba(2, 0, 36, 1) 0%,
        rgba(33, 9, 121, 0.8) 35%,
        rgba(0, 168, 255, 1) 100%
      );;
    }
  }
  
  .input {
    padding: 10px;
    width: 60%;
    height: 30px;
    border: 2px solid black;
    border-radius: 5px;
  }
}
