.cardContainer {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-around;
    // padding: 20px;
    // background-color: #f0f0f0;
    background: rgb(2,0,36);
    background: linear-gradient(90deg, rgba(2,0,36,1) 0%, rgba(33,9,121,0.8045343137254902) 35%, rgba(0,168,255,1) 100%);
    padding-bottom: 100px;
  margin-bottom: 40px;
  }
  
  .card {
    background: linear-gradient(135deg, #f6d365 0%, #fda085 100%);
    border-radius: 15px;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
    margin: 20px;
    width: 250px;
    overflow: hidden;
    transition: transform 0.3s, box-shadow 0.3s;
    position: relative;
    top: 100px;
    
    &:hover {
      transform: scale(1.05);
      box-shadow: 0 6px 20px rgba(0, 0, 0, 0.3);
    }
  }
  
  .cardImage {
    width: 100%;
    height: 200px;
    // object-fit: cover;
    border-bottom: 2px solid #fff;
  }
  
  .cardContent {
    padding: 20px;
    // text-align: center;
    .btn{
      display: flex;
      gap:10px;
      Button{
        position: absolute;
        bottom: 3px;
        padding: 5px;
      }
      .left{
        margin-right: 30px;
      }
    }
  }
  
  .cardTitle {
    font-size: 1.8em;
    margin: 0;
    color: #fff;
    
    text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.2);
  }
  
  .cardText {
    font-size: 1em;
    color: #fff;
    margin: 10px 0;
  }
